import React from "react"
import styles from './Card.module.scss'

function Card(props) {
    const [isAddded, setIsAdded] = React.useState(true);

    const onClickPlus =  () => {
        setIsAdded(!isAddded)
    }

    React.useEffect(() => {
        ReactDOM.unmountComponentAtNode(this.Card)
    }, [isAddded])

    return (
        <div className={styles.card}>
            <img className="ml-15" src={props.imgUrl} height={170} width={150} />
            <h5>{props.title}</h5>
            <div className="d-flex justify-between align-center">
                <div className="d-flex flex-column">
                    <span>Количество оборудования:<b>{props.count}</b></span>
                </div>
                <button className="button" onClick={onClickPlus}>
                    <img height={32} width={32} src={isAddded ? "/img/btn/btn-plus.svg" : "/img/btn/btn-checked.svg"} />
                </button>
            </div>
        </div>
    );
};

export default Card;
