import Card from './components/Card'
import Header from './components/Header'
import Drawer from './components/Drawer/Drawer.js'
import React from 'react';
const arr = [
  [{"name":"name 1","imgUrl":"imgUrl 1","count":60,"id":"1"},{"name":"name 2","imgUrl":"imgUrl 2","count":56,"id":"2"},{"name":"name 3","imgUrl":"imgUrl 3","count":9,"id":"3"},{"name":"name 4","imgUrl":"imgUrl 4","count":16,"id":"4"},{"name":"name 5","imgUrl":"imgUrl 5","count":10,"id":"5"},{"name":"name 6","imgUrl":"imgUrl 6","count":99,"id":"6"},{"name":"name 7","imgUrl":"imgUrl 7","count":80,"id":"7"},{"name":"name 8","imgUrl":"imgUrl 8","count":84,"id":"8"},{"name":"name 9","imgUrl":"imgUrl 9","count":52,"id":"9"},{"name":"name 10","imgUrl":"imgUrl 10","count":60,"id":"10"}]
];

function App() {
  const [cardOpened, isOpened] = React.useState(false);


  return (<div className="App clear">
    <Drawer />
    <Header/>
    <div className="content p-40">
      <div className="mb-40 d-flex justify-between align-center">
        <h1 >Компании Участники</h1>
        <div className="Search-block d-flex align-center">
          <img src="/img/Btn/search.svg"></img>
          <input placeholder="Поиск..."></input>
        </div>
      </div>
      <div className='d-flex'>
        {
          arr.map((obj) => <Card title={obj.name} count={obj.count} imgUrl={obj.imgUrl} onClickCard={()=> cardOpened(!isOpened)}/>)
        }
      </div>
    </div> 
  </div>
  );
}
export default App;
