import Card from './components/Card'
import Header from './components/Header'
import Drawer from './components/Drawer/Drawer.js'
import React from 'react';
const arr = [
  { name: 'ЦВК Экспоцентр', count: 24, imgUrl: '/img/Expocentre-logo.svg' },
  { name: 'НьюЭкспо', count: 28, imgUrl: '/img/expo-logo.svg' },
  { name: 'Abbott', count: 20, imgUrl: '/img/Abbott_Laboratories_Logo_black.svg' },
  { name: 'КВЦ «Сокольники»', count: 36, imgUrl: '/img/sokolniki-mpc-2.svg' }
];

function App() {
  const [items, setItems] = React.useState([]);
  const [cardOpened, isOpened] = React.useState(false);

  fetch("https://6543ae8501b5e279de20cbb5.mockapi.io/items").then((res) => {
    return res.json();
  }).then((json) =>{
    setItems(json);
  })
  return (<div className="App clear">
    <Drawer />
    <Header/>
    <div className="content p-40">
      <div className="mb-40 d-flex justify-between align-center">
        <h1 >Компании Участники</h1>
        <div className="Search-block d-flex align-center">
          <img src="/img/Btn/search.svg"></img>
          <input placeholder="Поиск..."></input>
        </div>
      </div>
      <div className="contentItem d-flex flex-wrapper">
        {
          arr.map((obj) => <Card title={obj.name} count={obj.count} imgUrl={obj.imgUrl} onClickCard={()=> cardOpened(!isOpened)}/>)
        }
      </div>
    </div> 
  </div>
  );
}
export default App;
